package cc.abase.demo.component.verification

import cc.ab.base.ext.click
import cc.ab.base.ext.pressEffectAlpha
import cc.ab.base.ext.toast
import cc.ab.base.ext.xmlToString
import cc.abase.demo.R
import cc.abase.demo.component.comm.CommBindFragment
import cc.abase.demo.databinding.FragmentVerificationBinding

/**
 * Author:Khaos116
 * Date:2022/3/9
 * Time:18:37
 */
class VerificationFragment : CommBindFragment<FragmentVerificationBinding>() {
  private var bg64 =
    "data:image/png;base64,"
  private var frame64 =
    "data:image/png;base64,"
  private var top = 22

  override fun lazyInit() {
    viewBinding.flTitle.commTitleBack.pressEffectAlpha()
    viewBinding.flTitle.commTitleText.text = R.string.滑动验证码.xmlToString()
    viewBinding.flTitle.commTitleBack.click { mActivity.onBackPressed() }
    viewBinding.verificationView.setDragIv(R.mipmap.ic_launcher)
    viewBinding.verificationView.mCallBack = {
      "drag x distance:$it".toast()
      viewBinding.verificationView.postDelayed({ viewBinding.verificationView.resetMove() }, 2000)
    }
    viewBinding.verificationView.setData(bg64, frame64, top)
  }
}